// Extract image data from webhook
const inputData = $input.first();

// Log the input for debugging
console.log('Input data structure:', JSON.stringify(inputData));

// Try to extract image data from different possible locations
let imageData = null;
let filename = null;
let contentType = null;

// Check if data is in json property
if (inputData.json) {
  console.log('Checking json property:', JSON.stringify(inputData.json));
  imageData = inputData.json.image;
  filename = inputData.json.filename;
  contentType = inputData.json.contentType;
}

// Check if data is directly in the input
if (!imageData && inputData.image) {
  console.log('Checking direct input properties');
  imageData = inputData.image;
  filename = inputData.filename;
  contentType = inputData.contentType;
}

// Check if data is in body property
if (!imageData && inputData.body) {
  console.log('Checking body property');
  let body;
  try {
    body = typeof inputData.body === 'string' ? JSON.parse(inputData.body) : inputData.body;
    console.log('Parsed body:', JSON.stringify(body));
    imageData = body.image;
    filename = body.filename;
    contentType = body.contentType;
  } catch (error) {
    console.log('Error parsing body:', error.message);
  }
}

// Check if data is in the root of inputData
if (!imageData && typeof inputData === 'object') {
  console.log('Checking root inputData properties');
  for (const key in inputData) {
    if (key !== 'json' && key !== 'body' && typeof inputData[key] === 'string' && inputData[key].startsWith('data:image')) {
      console.log('Found potential image data in property:', key);
      imageData = inputData[key];
      break;
    }
  }
}

// Validate input
if (!imageData) {
  console.log('No image data found in input');
  throw new Error('No image data provided');
}

console.log('Image data found, length:', imageData.length);
console.log('Filename:', filename);
console.log('Content type:', contentType);

// Return processed data
return {
  json: {
    imageBase64: imageData,
    filename: filename || 'unknown.jpg',
    contentType: contentType || 'image/jpeg',
    timestamp: new Date().toISOString()
  }
};

// Analyze Google Vision results for agricultural patterns
const visionResults = $input.first().json;
const labels = visionResults.responses[0]?.labelAnnotations || [];

// Define agricultural keywords
const agriculturalKeywords = [
  'plant', 'leaf', 'flower', 'fruit', 'vegetable', 'crop',
  'disease', 'fungus', 'bacteria', 'virus', 'pest', 'insect',
  'brown', 'yellow', 'spot', 'blight', 'rot', 'wilt', 'mold'
];

// Analyze labels for disease indicators
let diseaseIndicators = [];
let confidence = 0;
let detectedDisease = 'Healthy';
let severity = 'Low';

labels.forEach(label => {
  const description = label.description.toLowerCase();
  const score = label.score;

  // Check for disease indicators
  agriculturalKeywords.forEach(keyword => {
    if (description.includes(keyword)) {
      diseaseIndicators.push({
        keyword: keyword,
        description: label.description,
        confidence: score
      });

      // Update confidence and disease detection
      if (score > confidence) {
        confidence = score;
      }
    }
  });
});

// Determine disease type based on indicators
if (diseaseIndicators.some(indicator => 
  ['brown', 'spot', 'blight'].includes(indicator.keyword)
)) {
  detectedDisease = 'Leaf Blight';
  severity = confidence > 0.7 ? 'High' : 'Moderate';
} else if (diseaseIndicators.some(indicator => 
  ['yellow', 'wilt'].includes(indicator.keyword)
)) {
  detectedDisease = 'Yellow Wilt';
  severity = confidence > 0.7 ? 'High' : 'Moderate';
} else if (diseaseIndicators.some(indicator => 
  ['mold', 'fungus'].includes(indicator.keyword)
)) {
  detectedDisease = 'Fungal Infection';
  severity = confidence > 0.7 ? 'High' : 'Moderate';
}

// Generate treatment recommendations
const treatments = [];
const prevention = [];

if (detectedDisease !== 'Healthy') {
  treatments.push('Apply appropriate fungicide or pesticide');
  treatments.push('Remove infected plant parts');
  treatments.push('Improve air circulation around plants');

  prevention.push('Avoid overhead watering');
  prevention.push('Maintain proper plant spacing');
  prevention.push('Use disease-resistant varieties');
  prevention.push('Regular monitoring and early detection');
}

return {
  json: {
    analysis: {
      disease: detectedDisease,
      confidence: Math.round(confidence * 100),
      severity: severity,
      indicators: diseaseIndicators,
      treatments: treatments,
      prevention: prevention,
      timestamp: new Date().toISOString()
    }
  }
};
